$(document).ready(function(){$("#loginButton").click(function(d){$("#errorAlert").fadeOut("fast");$(this).prop("disabled",true);d.preventDefault();var c=$("#inputMail").val();var b=$("#inputPassword").val();var a="";if(c<2){a+="<li>Please enter your E-Mail</li>"}if(b<8){a+="<li>Please enter a correct Password</li>"}if(a!=""){$("#errorText").html(a);$("#errorAlert").fadeIn("fast");$(this).prop("disabled",false)}else{$.ajax({type:"POST",data:{email:c,password:b},url:"modul/login/loginUser.php",success:function(e){if(e){$("#errorText").html(e);$("#errorAlert").fadeIn("fast");$("#loginButton").prop("disabled",false)}else{$("#successText").html("Successfully Loggedin.");$("#successAlert").fadeIn("fast");parent.window.location.reload()}}})}})});