$(document).ready(function(){$("#saveButton").click(function(){$("#errorAlert").fadeOut("fast");$(this).prop("disabled",true);event.preventDefault();var h=$("#firstname").val();var g=$("#lastname").val();var d=$("#age").val();var b=$("#height").val();var a=$("#aim_date").val();var c=$("#aim_weight").val();var f=$("#gender").val();var e="";if(h<2){e+="<li>Please enter a Firstname</li>"}if(g<8){e+="<li>Please enter a Lastname</li>"}if(d<8){e+="<li>Please enter your Age</li>"}if(b<8){e+="<li>Please enter your Height</li>"}if(a!=""||c!=""){if(a<8){e+="<li>Please enter a Aim-Date</li>"}if(c<8){e+="<li>Please enter a Aim-Weight</li>"}}if(f<0||f>1){e+="<li>Please select a gender</li>"}if(e!=""){$("#errorText").html(e);$("#errorAlert").fadeIn("fast");$(this).prop("disabled",false)}else{$.ajax({type:"POST",data:{firstname:h,lastname:g,age:d,height:b,aim_date:a,aim_weight:c,gender:f},url:"modul/settings/updateEntry.php",success:function(i){if(i){$("#errorText").html(i);$("#errorAlert").fadeIn("fast");$("#saveButton").prop("disabled",false)}else{$("#successText").html("Successfully Saved.");$("#successAlert").fadeIn("fast").delay(2000).fadeOut("fast");$("#saveButton").prop("disabled",false)}}})}})});