function delEntry(a){$.ajax({type:"POST",data:{entryID:a},url:"modul/weights/removeWeight.php",success:function(b){if(b){$("#errorText").html(b);$("#errorAlert").fadeIn("fast")}else{$("#successText").html("Successfully Removed.");$("#successAlert").fadeIn("fast").delay(2000).fadeOut("fast");$(".deletableEntry").each(function(){if($(this).attr("entryID")==a){$(this).slideUp("slow")}})}}})};